import React from 'react';

interface ButtonProps {
  children: React.ReactNode;
  className?: string;
  onClick?: () => void;
}

export default function Button({ children, className = '', onClick }: ButtonProps) {
  // 添加hover:text-green-500类来实现悬浮时文字变绿
  return (
    <button 
      className={`bg-blue-500 text-blue-50 px-4 py-2 rounded-md hover:text-green-500 transition-colors ${className}`}
      onClick={onClick}
    >
      {children}
    </button>
  );
}

import styled from 'styled-components';

const StyledButton = styled.button`
  background-color: #3b82f6;
  color: #eff6ff;
  padding: 0.5rem 1rem;
  border-radius: 0.375rem;
  transition: color 0.2s;

  &:hover {
    color: #22c55e; /* 绿色 */
  }
`;

export default StyledButton;

import * as React from "react"

export function Spacer() {
  return <div className="flex-1" />
}

const Router = require('@koa/router')
const axios = require('axios')
const router = new Router()

// Fill in your client ID and client secret that you obtained
// while registering the application
const clientID = '7e015d8ce32370079895'
const clientSecret = '2b976af0e6b6ceea2b1554aa31d1fe94ea692cd9'

router.get('/oauth/redirect', async ctx => {
  const requestToken = ctx.request.query.code
  console.log('authorization code:', requestToken)

  const tokenResponse = await axios({
    method: 'post',
    url: 'https://github.com/login/oauth/access_token?' +
         `client_id=${clientID}&` +
         `client_secret=${clientSecret}&` +
         `code=${requestToken}`,
    headers: {
      accept: 'application/json',
    },
  })

  const accessToken = tokenResponse.data.access_token
  const data = tokenResponse.data
  console.log('token response:', data)
  console.log(`access token: ${accessToken}`)

  const result = await axios({
    method: 'get',
    url: `https://api.github.com/user`,
    headers: {
      accept: 'application/json',
      Authorization: `token ${accessToken}`,
    },
  })
  console.log(result.data)
  const name = result.data.name

  ctx.response.redirect(`/welcome.html?name=${name}`)
})

module.exports = router
